    if (alert.owner !== req.user.userId) {
        return res.status(403).send("Unauthorized to delete this alert.");
    }
    const deletedAlert = await Alert.findByIdAndDelete(alertId);
    res.status(200).json({ message: 'Alert removed' });
});
