router.post('/check/:deviceId', async (req, res) => {
    // Validate authentication
    if (!req.user || !req.user.userId) {
        return res.status(401).send('Unauthorized');
    }

    const { deviceId } = req.params;
    const { currentValue } = req.body;

    // Validate inputs
    if (!deviceId || currentValue === undefined) {
        return res.status(400).send('Missing required parameters');
    }

    // Validate deviceId format to prevent NoSQL injection
    if (!/^[0-9a-fA-F]{24}$/.test(deviceId)) {
        return res.status(400).send('Invalid device ID format');
    }

    try {
        const alerts = await Alert.find({ deviceId }).exec();

        for (let i = 0; i < alerts.length; i++) {
            const alert = alerts[i];
            let triggered = false;

            if (alert.condition === 'greater_than' && currentValue > alert.threshold) {
                triggered = true;
            } else if (alert.condition === 'equals' && currentValue === alert.threshold) {
                triggered = true;
            } else if (alert.condition === 'less_than' && currentValue < alert.threshold) {
                triggered = true;
            }

            if (triggered) {
                console.log(`Alert triggered for device ${deviceId}`);

                // Get user email from database instead of hardcoding
                const user = await User.findById(alert.owner);
                const userEmail = user ? user.email : 'admin@example.com';

                try {
                    await sendEmail({
                        to: userEmail,
                        subject: 'Device Alert!',
                        message: alert.message
                    });
                } catch (emailError) {
                    console.error('Failed to send email:', emailError);
                }
            }
        }

        res.send('Device check complete.');
    } catch (error) {
        console.error('Error checking device alerts:', error);
        res.status(500).send('Internal server error');
    }
});
