    String orderDetails = "ID: " + order.getItemId() + ", Name: " + order.getCustomerName();
    Path orderFile = Paths.get(System.getProperty("java.io.tmpdir"), "last_order.txt");
    try {
        Files.write(orderFile, orderDetails.getBytes());
    } catch (IOException e) {
        // Log the error and potentially rethrow or handle appropriately
        System.err.println("Failed to save order to file: " + e.getMessage());
        throw new RuntimeException("Order persistence failed", e);
    }
}

